	/**
	 * Method perform an INSERT on the database, given a Activitats or Criteria object.
	 *
	 * @param      mixed $values Criteria or Activitats object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BaseActivitatsPeer:doInsert:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BaseActivitatsPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(ActivitatsPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Activitats object
		}

		if ($criteria->containsKey(ActivitatsPeer::ACTIVITATID) && $criteria->keyContainsValue(ActivitatsPeer::ACTIVITATID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.ActivitatsPeer::ACTIVITATID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
