			$con->beginTransaction();
			$affectedRows += ActivitatsPeer::doOnDeleteCascade(new Criteria(ActivitatsPeer::DATABASE_NAME), $con);
			$affectedRows += BasePeer::doDeleteAll(ActivitatsPeer::TABLE_NAME, $con);
