    foreach (sfMixer::getCallables('BaseAgendaactivitatsPeer:doCount:doCount') as $callable)
    {
      call_user_func($callable, 'BaseAgendaactivitatsPeer', $criteria, $con);
    }


		$stmt = BasePeer::doCount($criteria, $con);

		if ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$count = (int) $row[0];
		} else {
			$count = 0; // no rows returned; we infer that means 0 matches.
		}
		$stmt->closeCursor();
