	/**
	 * Selects a collection of Agendaactivitats objects pre-filled with all related objects.
	 *
	 * @param      Criteria  $c
	 * @param      PropelPDO $con
	 * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
	 * @return     array Array of Agendaactivitats objects.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAll(Criteria $c, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{

    foreach (sfMixer::getCallables('BaseAgendaactivitatsPeer:doSelectJoinAll:doSelectJoinAll') as $callable)
    {
      call_user_func($callable, 'BaseAgendaactivitatsPeer', $c, $con);
    }


		$c = clone $c;

		// Set the correct dbName if it has not been overridden
		if ($c->getDbName() == Propel::getDefaultDB()) {
			$c->setDbName(self::DATABASE_NAME);
		}

		AgendaactivitatsPeer::addSelectColumns($c);
		$startcol2 = (AgendaactivitatsPeer::NUM_COLUMNS - AgendaactivitatsPeer::NUM_LAZY_LOAD_COLUMNS);

		CluberPeer::addSelectColumns($c);
		$startcol3 = $startcol2 + (CluberPeer::NUM_COLUMNS - CluberPeer::NUM_LAZY_LOAD_COLUMNS);

		$c->addJoin(array(AgendaactivitatsPeer::CLUBER_IDCLUBBER,), array(CluberPeer::IDCLUBBER,), $join_behavior);
		$stmt = BasePeer::doSelect($c, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = AgendaactivitatsPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = AgendaactivitatsPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$omClass = AgendaactivitatsPeer::getOMClass();

				$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
				$obj1 = new $cls();
				$obj1->hydrate($row);
				AgendaactivitatsPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

			// Add objects for joined Cluber rows

			$key2 = CluberPeer::getPrimaryKeyHashFromRow($row, $startcol2);
			if ($key2 !== null) {
				$obj2 = CluberPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$omClass = CluberPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					CluberPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 loaded

				// Add the $obj1 (Agendaactivitats) to the collection in $obj2 (Cluber)
				$obj2->addAgendaactivitats($obj1);
			} // if joined row not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


