		if ($this->isColumnModified(DescripcionsPeer::ACTIVITATS_ACTIVITATID)) $criteria->add(DescripcionsPeer::ACTIVITATS_ACTIVITATID, $this->activitats_activitatid);
		if ($this->isColumnModified(DescripcionsPeer::DESCRIPCIO)) $criteria->add(DescripcionsPeer::DESCRIPCIO, $this->descripcio);
		if ($this->isColumnModified(DescripcionsPeer::TIPUS)) $criteria->add(DescripcionsPeer::TIPUS, $this->tipus);
		if ($this->isColumnModified(DescripcionsPeer::ACTIVA)) $criteria->add(DescripcionsPeer::ACTIVA, $this->activa);
		if ($this->isColumnModified(DescripcionsPeer::IMATGE)) $criteria->add(DescripcionsPeer::IMATGE, $this->imatge);
		if ($this->isColumnModified(DescripcionsPeer::PDF)) $criteria->add(DescripcionsPeer::PDF, $this->pdf);
		if ($this->isColumnModified(DescripcionsPeer::PUBLICAWEB)) $criteria->add(DescripcionsPeer::PUBLICAWEB, $this->publicaweb);
		if ($this->isColumnModified(DescripcionsPeer::TWEB)) $criteria->add(DescripcionsPeer::TWEB, $this->tweb);
		if ($this->isColumnModified(DescripcionsPeer::DWEB)) $criteria->add(DescripcionsPeer::DWEB, $this->dweb);
