	 */
	public function getActivitats(PropelPDO $con = null)
	{
		if ($this->aActivitats === null && ($this->activitats_activitatid !== null)) {
			$c = new Criteria(ActivitatsPeer::DATABASE_NAME);
			$c->add(ActivitatsPeer::ACTIVITATID, $this->activitats_activitatid);
			$this->aActivitats = ActivitatsPeer::doSelectOne($c, $con);
			/* The following can be used additionally to
			   guarantee the related object contains a reference
			   to this object.  This level of coupling may, however, be
			   undesirable since it could result in an only partially populated collection
			   in the referenced object.
			   $this->aActivitats->addDescripcionss($this);
			 */
		}
		return $this->aActivitats;
	}

